fmApp.directive("ngMousedown",function(){var checkForTapTrigger,triggerTappedState;checkForTapTrigger=function($trigger){var $parent,isTapTrigger;isTapTrigger=$trigger.filter("[data-tap-trigger]").length;$parent=$trigger.closest("[data-tap-animate-source]");if(isTapTrigger&&$parent.length){return triggerTappedState($parent)}};triggerTappedState=function($parent){var delay;$parent.addClass("tapped");delay=$parent.attr("data-tap-transition-delay")||400;return setTimeout(function(){return $parent.removeClass("tapped")},delay)};return{restrict:"A",replace:false,link:function(scope,el,attrs){return el.on("mousedown",function(){return checkForTapTrigger(jQuery(this))})}}});